import {useState, useEffect, useRef} from "react";
import styled from "styled-components";
let sam = 0;
const
Wrapper = styled.div`
min-width: 100vw;
min-height: 100%;
flex: 1;
background-color: red;
transform-origin: left top;
font-size: var(--font-root-regular);
transform: scale(${(props) => props.scale})
`,
Scale = ({panel, children}) => {
  const [scale, setScale] = useState(1);
  useEffect(() => {
    if (sam > 8) return;
    const clientScroll = document.documentElement.scrollWidth,
          clientWidth = document.documentElement.clientWidth;
    console.log(`clientScroll ${clientScroll}`);
    console.log(`clientWidth ${clientWidth}`);
    if (clientScroll === clientWidth)
    if (clientScroll === clientWidth) return null;

    console.log("content will scale");
    console.log(clientScroll);
    console.log(clientWidth);

    // setTimeout(() => setScale(scale - 0.1), 500);
    setTimeout(() => setScale(scale - 0.05), 1000);
    ++sam;
  }, [scale]);

  useEffect(() => {
    const clientScroll = document.documentElement.scrollWidth,
          clientWidth = document.documentElement.clientWidth;
    if (clientScroll === clientWidth) return null;
    setScale(scale - 0.1);
    ++sam;
  }, [panel.data]);

  return (
    <Wrapper key={scale} scale={scale}>
      {children}
    </Wrapper>
  );
};

export {Scale};
