export
const
weight = (weight, unit, oldUnit) => {
  switch (unit) {
  case "kg":
    if (oldUnit === "gr") weight *= 1000;
    return Number.parseFloat(weight).toFixed(3);
  case "gr":
    return Number.parseFloat(weight / 1000).toFixed(3);
  case "lt":
    if (oldUnit === "gr") weight *= 1000;
    return Number.parseFloat(weight).toFixed(3);
  default:
    return weight;
  }
};
