import styled from "styled-components";

export
const
Root = styled.article`
min-width: 100vw;
min-height: 100vh;
flex-flow: column nowrap;
display: flex;
`,
Header = styled.header`
`,
Sidebar = styled.aside`
flex: 0;
display: flex;
`,
Main = styled.main`
flex: 1;
display: flex;
flex-flow: row nowrap;
`,
Footer = styled.footer`
grid-column: 1/2;
grid-row: 3/4;
`;
