import styled from "styled-components";

export
const
Root = styled.article`
min-width: 100vw;
min-height: 100vh;
flex-flow: column nowrap;
display: flex;
`,
Sidebar = styled.aside`
flex: 1;
max-width: 350px;
min-width: 150px;
`,
Main = styled.main`
flex: 1;
display: flex;
flex-flow: row nowrap;
`;
