import styled from "styled-components";

export
const
Root = styled.article`
min-width: 100vw;
min-height: 100vh;
flex-flow: column nowrap;
display: flex;
`,
Header = styled.header`
`,
Sidebar = styled.aside`
flex: 0;
display: flex;
`,
Main = styled.main`
flex: 1;
display: grid;
grid-template-columns: 1fr 1fr;
grid-template-rows: 1fr;
`,
Footer = styled.footer`
`;
