import {useEffect, useState} from "react";

export
const
useResizeEvent = () => {
  const [resize, setResize ] = useState(0);

  useEffect(() => {
    window.addEventListener("resize", () => {
      setResize((Math.random() + 1).toString(36).substring(7));
    });
  }, []);

  return resize;
};
