import {useResizeEvent} from "lib/hooks";
import {APP_STORE, PANEL_STORE} from "lib/stores";
import * as Layout from "./Layout";
import Header from "./Header";
import Footer from "./Footer";
import Sidebar from "./Sidebar";

export
const
App = () => {
  const
  {app, setApp} = APP_STORE.useApp(),
  {panel, setPanel} = PANEL_STORE.usePanel(),
  resize = useResizeEvent();

  console.log(app);
  console.log(panel);
  return (
    <APP_STORE.appContext.Provider value={{...app, setApp}}>
      <PANEL_STORE.panelContext.Provider value={{panel, setPanel}}>
        <Layout.Root key={resize}>
          <Layout.Header><Header/></Layout.Header>
          <Layout.Sidebar><Sidebar/></Layout.Sidebar>
          <Layout.Main id="main-content"></Layout.Main>
          <Layout.Footer><Footer/></Layout.Footer>
        </Layout.Root>
      </PANEL_STORE.panelContext.Provider>
    </APP_STORE.appContext.Provider>
  );
};
