import {useRef, useState, useEffect} from "react";
import styled from "styled-components";

const S = {};
S.Toggle = styled.div`
flex: 1;
display: flex;
width: ${props => props.dimensions.width && props.dimensions.width + "px"};
position: relative;
transform: ${props => props.toggled && "translateX(-100%)"};
transition: transform 2s;

::before{
content: "";
background-image: url("right-arrow-angle.png");
background-size: cover;
background-repeat: no-repeat;

box-sizing: content-box;
padding: 10px 10px;
width: 30px;
height: 30px;
cursor: pointer;

position: absolute;
top: 50%;
right: ${props => props.toggled ? "-43px" : "-50px"};
transform: ${props => props.toggled ? "rotate() translate(0, 12%)" : "rotate(-180deg) translate(0, 0%)"};
transition: transform 2s, right 2s;
}
`;

export function Hide({children}) {
  const
  [toggle, setToggle] = useState(false),
  [dimensions, setDimensions] = useState({}),
  ref = useRef(null);

  useEffect(() => {
    const child = ref.current.firstElementChild;
    setDimensions({width: child.offsetWidth});
  }, []);

  function handleToggle(e) {
    if (e.target !== ref.current) return;
    setToggle(!toggle);
    const child = ref.current.firstElementChild;
    setDimensions({width: 0});
  }

  return (
    <S.Toggle ref={ref} dimensions={dimensions} toggled={toggle} onClick={handleToggle}>
      {children}
    </S.Toggle>
  );
}
